    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double produto = Double.valueOf(jTextValor.getText());
        double parcela = Double.valueOf(jTextParcelas.getText());
        double valor = (produto/parcela);
        jTextFinal.setText(String.valueOf(valor));
    }//GEN-LAST:event_jButton1ActionPerformed

